targets.rawSql:
SELECT MONTHNAME(created_at), SUM(AMOUNT) FROM Transactions where transactionType='Credit' 
AND YEAR(created_at) = YEAR(CURRENT_DATE()) and campusId = $campusId 
group by MONTHNAME(created_at);

SELECT MONTHNAME(created_at), SUM(AMOUNT) FROM myskool.Transactions where transactionType='Debit' 
AND YEAR(created_at) = YEAR(CURRENT_DATE()) and campusId = $campusId 
group by MONTHNAME(created_at);
